INSERT INTO games (id, title, platform, genre, price, rating, release_date, stock, description, cover_image, url_trailer, promo, popular) VALUES
('1', 'Cyberpunk 2077', 'PC', 'RPG', 59.99, 4.5, '2020-12-10', 12, 'Incarnez V, un mercenaire dans la mégalopole futuriste de Night City. Personnalisez votre cyberware, vos compétences et votre style de jeu pour survivre dans une ville obsédée par le pouvoir et la technologie. Avec Keanu Reeves dans le rôle de Johnny Silverhand.', '/assets/games/cover/cyberpunk.jpg', 'https://www.youtube.com/watch?v=8X2kIfS6fb8', true, false),
('2', 'Red Dead Redemption 2', 'PC', 'Aventure', 49.99, 4.7, '2018-10-26', 18, 'Vivez l''Amérique à la fin de l''ère du Far West. Incarnez Arthur Morgan, un hors-la-loi en fuite avec sa bande. Chassez, pêchez, braquez des banques et survivez dans un monde ouvert immense et réaliste. Une histoire épique sur la loyauté et la rédemption.', '/assets/games/cover/rdr2.jpg', 'https://www.youtube.com/watch?v=gmA6MrX81z4', false, false),
('3', 'The Witcher 3: Wild Hunt', 'PC', 'RPG', 39.99, 4.9, '2015-05-19', 20, 'Geralt de Rivia, chasseur de monstres légendaire, part à la recherche de Ciri, l''enfant de la prophétie. Explorez un continent déchiré par la guerre, combattez des créatures redoutables et prenez des décisions qui changeront le cours de l''histoire.', '/assets/games/cover/witcher3.jpg', 'https://www.youtube.com/watch?v=c0i88t0Kacs', false, true),
('4', 'Assassin''s Creed Valhalla', 'PC', 'Action', 54.99, 4.8, '2020-11-12', 10, 'Devenez Eivor, un fier guerrier viking élevé dans les légendes de bataille et de gloire. Explorez l''Angleterre médiévale, menez des raids, construisez votre colonie et gagnez votre place au Valhalla dans ce chapitre épique de la saga Assassin''s Creed.', '/assets/games/cover/valhalla.jpg', 'https://www.youtube.com/watch?v=ssrNcwxALS4', false, true),
('5', 'Grand Theft Auto V', 'PC', 'Action', 29.99, 4.8, '2013-09-17', 15, 'Explorez Los Santos, une métropole éclatante dans l''État de San Andreas. Incarnez trois criminels aux histoires entrelacées : Michael, Franklin et Trevor. Une satire mordante de la société américaine moderne avec un mode histoire captivant et GTA Online illimité.', '/assets/games/cover/gtav.jpg', 'https://www.youtube.com/watch?v=QkkoHAzjnUs', true, true),
('6', 'Call of Duty: Warzone', 'PC', 'FPS', 57.00, 4.8, '2020-12-22', 10, 'Battle royale intense dans l''univers de Call of Duty. Affrontez jusqu''à 150 joueurs dans la carte massive de Verdansk. Récupérez des contrats, personnalisez vos armes et soyez le dernier survivant dans cette expérience de combat tactique.', '/assets/games/cover/cod.jpg', 'https://www.youtube.com/watch?v=0E44DClsX5Q', false, true),
('7', 'Mortal Kombat 11', 'PC', 'Fighting', 44.99, 4.6, '2021-05-21', 15, 'Le combat ultime revient avec des graphismes époustouflants et une violence extrême. Personnalisez vos combattants préférés, découvrez une nouvelle histoire temporelle et maîtrisez des combos dévastateurs dans le jeu de combat le plus sanglant de la planète.', '/assets/games/cover/mk11.jpg', 'https://www.youtube.com/watch?v=UoTams2yc0s', false, false),
('8', 'Street Fighter 6', 'PC', 'Fighting', 59.99, 4.6, '2023-09-20', 15, 'La révolution du combat arrive ! Avec un nouveau système de combat innovant, un mode monde ouvert inédit et des graphismes next-gen. Rejoignez le Fighting Ground, le World Tour et le Battle Hub pour l''expérience Street Fighter ultime.', '/assets/games/cover/sf6.jpg', 'https://www.youtube.com/watch?v=1tBUsXIkG1A', false, false),
('9', 'EA Sports FC 24', 'PC', 'Sport', 69.99, 4.8, '2024-09-28', 10, 'L''ère nouvelle du football vidéo commence. Avec la technologie HyperMotionV, l''Opta optimisée et les styles de jeu PlayStyles, vivez le jeu le plus réaliste jamais créé. Plus de 19,000 joueurs, 700 équipes et 30 ligues.', '/assets/games/cover/fc24.jpg', 'https://www.youtube.com/watch?v=XhP3Xh4LMA8', false, true),
('10', 'Baldur''s Gate 3', 'PC', 'RPG', 59.99, 4.9, '2023-08-03', 8, 'Plongez dans un RPG épique où chaque choix compte. Créez votre personnage, formez votre groupe et explorez les Royaumes Oubliés dans cette aventure aux possibilités infinies. Basé sur les règles de D&D 5e, avec un système de combat tactique et des milliers de dialogues.', '/assets/games/cover/baldursgate3.jpg', 'https://www.youtube.com/watch?v=OcP0WdH7rTs', true, true),
('11', 'Valorant', 'PC', 'FPS', 39.00, 4.5, '2020-06-02', 75, 'FPS tactique 5v5 qui mélange précision au tir et compétences d''agents uniques. Chaque agent possède des capacités spéciales qui peuvent changer le cours d''une manche. Stratégie, teamwork et skill sont la clé pour dominer.', '/assets/games/cover/valorant.jpg', 'https://www.youtube.com/watch?v=lWr6dhTcu-E', false, false),
('12', 'Starfield', 'PC', 'RPG', 69.99, 4.3, '2023-09-06', 14, 'Le premier nouvel univers de Bethesda en 25 ans. Explorez plus de 1000 planètes, personnalisez votre vaisseau, recrutez un équipage et découvrez les secrets de la galaxie dans ce RPG spatial épique.', '/assets/games/cover/starfield.jpg', 'https://www.youtube.com/watch?v=kfYEiTdsyas', false, false),
('13', 'Diablo IV', 'PC', 'RPG', 69.99, 4.6, '2023-06-06', 11, 'Retournez dans le monde sombre de Sanctuary dans ce nouvel opus de Diablo. Affrontez les forces du mal, personnalisez votre héros avec des compétences et équipements légendaires, et explorez un monde ouvert persistant rempli de donjons et de secrets.', '/assets/games/cover/diablo4.jpg', 'https://www.youtube.com/watch?v=9bRWIdOMfro', false, false),
('14', 'Forza Horizon 5', 'PC', 'Racing', 59.99, 4.8, '2021-11-09', 9, 'Course en monde ouvert au Mexique avec des centaines de voiles. Explorez des paysages époustouflants, participez à des festivals de musique et devenez une star de la course dans le plus grand Horizon à ce jour.', '/assets/games/cover/forza5.jpg', 'https://www.youtube.com/watch?v=FYH9n37B7Yw', true, true),
('15', 'Dying Light 2: Stay Human', 'PC', 'RPG', 49.99, 4.8, '2023-09-28', 10, 'Survie dans un monde post-apocalyptique ravagé par des zombies. Utilisez le parkour pour explorer la ville, combattez des infectés et des factions hostiles, et prenez des décisions qui affectent le monde autour de vous.', '/assets/games/cover/dyinglight.jpg', 'https://www.youtube.com/watch?v=UwJAAy7tPhE', false, true),
('16', 'God of War Ragnarök', 'PlayStation 5', 'Action', 69.99, 4.8, '2022-11-09', 10, 'La suite épique de l''histoire de Kratos et Atreus dans la mythologie nordique. Affrontez des dieux et des créatures légendaires tout en explorant les Neuf Royaumes dans cette aventure émotionnelle intense.', '/assets/games/cover/gow.jpg', 'https://www.youtube.com/watch?v=EE-4GvjKcfs', false, true),
('17', 'The Last of Us Part II', 'PlayStation 5', 'Action', 59.99, 4.9, '2020-06-19', 8, 'Une aventure émotionnelle intense dans un monde post-apocalyptique. Suivez Ellie dans sa quête de vengeance à travers l''Amérique dévastée, où chaque décision a un prix et chaque combat est une lutte pour la survie.', '/assets/games/cover/lastofus2.jpg', 'https://www.youtube.com/watch?v=btmN-bWwv0A', false, true),
('18', 'EA Sports FC 25', 'PlayStation 5', 'Sport', 79.99, 4.3, '2024-09-27', 22, 'La nouvelle génération de football avec HyperMotionV et une IA améliorée. Profitez d''une expérience de jeu ultra-réaliste avec des animations fluides, une physique avancée et des modes de jeu innovants.', '/assets/games/cover/fifa25.jpg', 'https://www.youtube.com/watch?v=pBM2xyco_Kg', true, false),
('19', 'Spider-Man 2', 'PlayStation 5', 'Action', 69.99, 4.8, '2023-10-20', 12, 'Incarnez Peter Parker et Miles Morales dans cette aventure arachnéenne épique. Affrontez de nouveaux villains dangereux, explorez New York élargi et maîtrisez de nouvelles capacités dans le combat le plus dynamique de la série.', '/assets/games/cover/spiderman2.jpg', 'https://www.youtube.com/watch?v=qIQ3xNqkVC4', false, true),
('20', 'Horizon Forbidden West', 'PlayStation 5', 'Action', 69.99, 4.7, '2022-02-18', 14, 'Explorez l''Ouest interdit dans cette suite épique d''Horizon Zero Dawn. Aloy découvre de nouvelles terres, affronte des machines plus dangereuses et révèle des secrets qui pourraient changer le destin du monde.', '/assets/games/cover/horizon2.jpg', 'https://www.youtube.com/watch?v=Lq594XmpPBg', false, false),
('21', 'Final Fantasy XVI', 'PlayStation 5', 'RPG', 69.99, 4.6, '2023-06-22', 9, 'Un nouveau chapitre épique dans la saga Final Fantasy. Plongez dans un monde de magie et de conflits politiques, où les Égides, gardiens de cristaux, s''affrontent dans des combats spectaculaires.', '/assets/games/cover/ff16.jpg', 'https://www.youtube.com/watch?v=gV5rIW1Qums', false, false),
('22', 'Demon''s Souls', 'PlayStation 5', 'RPG', 79.99, 4.7, '2020-11-12', 7, 'Remake du classique de FromSoftware avec des graphismes next-gen. Affrontez des démons dans le royaume de Boletaria, améliorez vos compétences et survivez à l''un des jeux les plus difficiles et gratifiants jamais créés.', '/assets/games/cover/demonssouls.jpg', 'https://www.youtube.com/watch?v=2TMs2E6cms4', false, false),
('23', 'Ratchet & Clank: Rift Apart', 'PlayStation 5', 'Action', 69.99, 4.8, '2021-06-11', 11, 'Aventure interdimensionnelle avec des graphismes incroyables. Voyagez entre les dimensions, utilisez des armes créatives et sauvez la galaxie dans cette aventure palpitante de Ratchet et Clank.', '/assets/games/cover/ratchet.jpg', 'https://www.youtube.com/watch?v=ai3o0XtrnM8', true, true),
('24', 'Returnal', 'PlayStation 5', 'Action', 69.99, 4.4, '2021-04-30', 6, 'Jeu de tir roguelike third-person avec une boucle temporelle. Explorez une planète étrangère en constante évolution, où chaque mort vous ramène au début mais avec de nouvelles connaissances et possibilités.', '/assets/games/cover/returnal.jpg', 'https://www.youtube.com/watch?v=ov4fJmGCsZM', false, false),
('25', 'Gran Turismo 7', 'PlayStation 5', 'Racing', 69.99, 4.6, '2022-03-04', 13, 'Simulation de course ultime avec des centaines de voitures. Profitez de graphismes photoréalistes, d''une physique avancée et d''une collection complète de voitures dans l''expérience Gran Turismo la plus aboutie.', '/assets/games/cover/gt7.jpg', 'https://www.youtube.com/watch?v=1tBUsXIkG1A', false, false),
('26', 'Ghost of Tsushima Director''s Cut', 'PlayStation 5', 'Action', 69.99, 4.8, '2021-08-20', 10, 'Édition complète du chef-d''œuvre de Sucker Punch Productions. Devenez Jin Sakai, un samurai luttant pour libérer l''île de Tsushima de l''invasion mongole en utilisant des techniques de combat traditionnelles et de nouvelles méthodes de ghost.', '/assets/games/cover/ghost.jpg', 'https://www.youtube.com/watch?v=A5gVt028Hww', false, true),
('27', 'Death Stranding Director''s Cut', 'PlayStation 5', 'Action', 59.99, 4.3, '2021-09-24', 8, 'Édition étendue du jeu unique de Hideo Kojima. Incarnez Sam Porter Bridges dans un monde post-apocalyptique où vous devez reconnecter une Amérique dévastée en livrant des colis et en évitant des entités spectrales.', '/assets/games/cover/deathstranding.jpg', 'https://www.youtube.com/watch?v=piIgkJWDuQg', true, false),
('28', 'Sackboy: A Big Adventure', 'PlayStation 5', 'Platformer', 59.99, 4.5, '2020-11-12', 15, 'Aventure coopérative mignonne et amusante avec Sackboy. Parcourez des mondes créatifs, résolvez des énigmes et collectionnez des objets dans cette aventure plateforme colorée et imaginative.', '/assets/games/cover/sackboy.jpg', 'https://www.youtube.com/watch?v=ZOk3fj5ujNM', false, false),
('29', 'Kena: Bridge of Spirits', 'PlayStation 5', 'Action', 39.99, 4.6, '2021-09-21', 11, 'Aventure d''action-aventure avec un art magnifique et des esprits mignons. Aidez les esprits à passer dans l''au-delà en utilisant vos pouvoirs magiques et vos compagnons Rot dans ce monde enchanté.', '/assets/games/cover/kena.jpg', 'https://www.youtube.com/watch?v=pWh5388AEHw', false, false),
('30', 'The Last of Us Part I', 'PlayStation 5', 'Action', 69.99, 4.8, '2022-09-02', 9, 'Remake du chef-d''œuvre original avec des graphismes next-gen. Revivez l''histoire émotionnelle de Joel et Ellie dans un monde post-apocalyptique, avec des graphismes repensés et une jouabilité améliorée.', '/assets/games/cover/lastofus1.jpg', 'https://www.youtube.com/watch?v=R2Ebc_OFeug', false, true),
('31', 'Halo Infinite', 'Xbox', 'FPS', 59.99, 4.5, '2021-12-08', 16, 'Le retour du Master Chief dans une aventure en monde ouvert. Explorez l''anneau Halo, combattez le Banished et découvrez les secrets de l''univers Halo dans cette épique aventure spatiale.', '/assets/games/cover/halo.jpg', 'https://www.youtube.com/watch?v=PyMlV5_HRWk', false, false),
('32', 'Gears 5', 'Xbox', 'Action', 39.99, 4.6, '2019-09-10', 12, 'Continuez la lutte contre l''Essaim dans cette suite épique. Incarnez Kait Diaz et découvrez les origines du Locust dans cette aventure third-person shooter intense et émotionnelle.', '/assets/games/cover/gears5.jpg', 'https://www.youtube.com/watch?v=APqBWniRQbQ', false, false),
('33', 'Microsoft Flight Simulator', 'Xbox', 'Simulation', 59.99, 4.7, '2021-07-27', 8, 'Simulateur de vol le plus réaliste avec le monde entier. Pilotez des avions légendaires, explorez la planète entière en temps réel et vivez l''expérience ultime du vol dans cette simulation incroyablement détaillée.', '/assets/games/cover/flightsim.jpg', 'https://www.youtube.com/watch?v=TYqJALPVn0Y', false, false),
('34', 'Sea of Thieves', 'Xbox', 'Aventure', 39.99, 4.4, '2018-03-20', 20, 'Jeu de pirates multijoueur en monde ouvert. Formez votre équipage, hissez les voiles et partez à l''aventure dans un monde pirate persistant rempli de trésors, de batailles navales et de mystères.', '/assets/games/cover/seaofthieves.jpg', 'https://www.youtube.com/watch?v=r5JIBaasuE8', true, false),
('35', 'Forza Motorsport', 'Xbox', 'Racing', 69.99, 4.5, '2023-10-10', 11, 'Simulation de course sur circuit avec une physique réaliste. Profitez de graphismes next-gen, d''une physique de conduite avancée et de centaines de voitures dans la simulation de course la plus réaliste de sa génération.', '/assets/games/cover/forzamotorsport.jpg', 'https://www.youtube.com/watch?v=em4gv1Ietko', false, false),
('36', 'Psychonauts 2', 'Xbox', 'Platformer', 59.99, 4.8, '2021-08-25', 9, 'Aventure psychédélique dans les esprits des personnages. Incarnez Raz et explorez les méandres de l''esprit humain dans cette aventure plateforme unique et créative remplie d''humour et de personnages mémorables.', '/assets/games/cover/psychonauts2.jpg', 'https://www.youtube.com/watch?v=mBi9rqCZ4os', false, true),
('37', 'Grounded', 'Xbox', 'Survival', 39.99, 4.6, '2022-09-27', 13, 'Survie dans un jardin où vous êtes de la taille d''une fourmi. Construisez des bases, combattez des insectes géants et explorez un monde dangereux dans cette aventure de survie unique en perspective.', '/assets/games/cover/grounded.jpg', 'https://www.youtube.com/watch?v=ciVgq5xO5G0', true, false),
('38', 'State of Decay 2', 'Xbox', 'Survival', 29.99, 4.3, '2018-05-22', 10, 'Survie zombie avec gestion de communauté et base building. Recrutez des survivants, construisez votre base et survivez à l''apocalypse zombie dans ce jeu de survie en monde ouvert.', '/assets/games/cover/stateofdecay2.jpg', 'https://www.youtube.com/watch?v=fhj5phhsqC0', false, false),
('39', 'Splatoon 3', 'Nintendo Switch', 'Shooter', 59.99, 4.7, '2022-09-09', 16, 'Jeu de tir multijoueur coloré avec des encres et des armes uniques. Personnalisez votre Inklings, participez à des batailles territoriales et découvrez de nouvelles armes et capacités dans cet univers coloré et compétitif.', '/assets/games/cover/splatoon3.jpg', 'https://www.youtube.com/watch?v=GUYDXVDLmns', false, false),
('40', 'Super Mario Party', 'Nintendo Switch', 'Party', 59.99, 4.5, '2018-10-05', 20, 'Jeu de plateau multijoueur avec mini-jeux. Réunissez vos amis et votre famille pour des parties endiablées avec vos personnages Mario préférés dans cette collection de mini-jeux amusants et compétitifs.', '/assets/games/cover/marioparty.jpg', 'https://www.youtube.com/watch?v=by_XTria-mQ', false, false);
